INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (193645, 'Thioridazine', 'Physostigmine (ophthalmic)', 'Moderate', 'Theoretically, anticholinergic agents and other agents with significant anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide) may antagonize the effects of topically administered cholinergic agents such as acetylcholine, carbachol, demecarium, echothiophate, isoflurophate, physostigmine, and pilocarpine. The proposed mechanism involves opposing pharmacodynamic action on muscarinic receptor sites in ocular tissue. This interaction is sometimes desirable and is the basis for using atropine in the treatment of excessive muscarinic side effects and cholinergic crisis induced by cholinergic overdose.', 'DDInter', 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193873/', '', 'Asenapine, Pimavanserin, Iloperidone, Amisulpride, Droperidol, Cariprazine, Paliperidone, Lurasidone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (193646, 'Thioridazine', 'Probucol', 'Major', 'Thioridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with thioridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 'Coadministration of thioridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193874/', '', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Cholestyramine, Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Cerivastatin, Lomitapide, Ezetimibe, Niacin, More', 1767369485);
