INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (193696, 'Dicoumarol', 'Tolbutamide', 'Moderate', 'Oral sulfonylureas may enhance or reduce the hypoprothrombinemic response to oral anticoagulants. The mechanism may be related to displacement from plasma protein binding sites. In addition, coumarin anticoagulants may cause an increase in blood levels of hypoglycemic agents, possibly by inhibiting their hepatic metabolism. Clinical data have been highly variable.', 'DDInter', 'The patient should be monitored for altered anticoagulation (PT/INR) and altered glycemic effect when either of these drugs is added to or removed from a patient''s regimen. Patients should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, tremor, hunger, weakness, or palpitations), and to promptly report any signs of bleeding (pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools) to their physician.', 'Metabolism', 'The patient should be monitored for altered anticoagulation (PT/INR) and altered glycemic effect when either of these drugs is added to or removed from a patient''s regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193924/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, More', 'Sorbitol, Caffeine, Edrophonium, Indocyanine green, Inulin, Hexaminolevulinate, Secretin human, Galactose, Macimorelin, Methacholine, Folic acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (193697, 'Dienestrol (topical)', 'Tolbutamide', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193925/', '', 'Sorbitol, Caffeine, Edrophonium, Indocyanine green, Inulin, Hexaminolevulinate, Secretin human, Galactose, Macimorelin, Methacholine, Folic acid, More', '', 1767369485);
