INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (194094, 'Vinblastine', 'Naxitamab', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', 'DDInter', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/194322/', '', 'Irinotecan', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (194095, 'Vinblastine', 'Radium Ra 223 dichloride', 'Moderate', 'The concomitant use of bone marrow depressants and radium Ra 223 dichloride may have additive myelosuppressive effects.', 'DDInter', 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms. The safety and efficacy of concomitant use of chemotherapy have not been established.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/194323/', '', '', '', 1767369485);
