INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236764, 'Adefovir dipivoxil', 'Metformin', 'Moderate', 'Theoretically, coadministration of adefovir dipivoxil with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of adefovir and/or the coadministered drug(s).  The mechanism is competitive inhibition of renal excretion.  Drugs that are thought to undergo active tubular secretion include metformin, cimetidine, ranitidine, procainamide, flecainide, quinidine, triamterene, midodrine, cidofovir, acyclovir, valacyclovir, tenofovir, ganciclovir, and valganciclovir.', 'DDInter', 'Patients receiving adefovir dipivoxil in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/238607/', '', 'Cabotegravir, Maraviroc, Zanamivir, Lenacapavir, Telaprevir, Rilpivirine, Etravirine, Cobicistat, Brincidofovir, Remdesivir, Doravirine, More', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Tolbutamide, Miglitol, More', 1767369485);
