INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236838, 'Dantrolene', 'Medroxyprogesterone acetate', 'Major', 'The use of estrogens may potentiate the risk of hepatotoxicity associated with dantrolene therapy.', 'DDInter', 'Dantrolene should be used with caution in patients, particularly females over 35 years of age, who are receiving concomitant estrogen therapy.  Liver function tests (SGOT, SGPT, alkaline phosphatase, total bilirubin) should be performed prior to and during dantrolene therapy at appropriate intervals, and the drug should generally be withheld if significant abnormalities are observed.  Patients treated with dantrolene should be advised to promptly contact their physician if they develop signs and symptoms of hepatocellular injury such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice.  The drug should be discontinued if hepatitis is suspected, since early detection and drug withdrawal will increase the likelihood of reversing the damage.  Following resolution of clinical and laboratory abnormalities, reinstitution of dantrolene therapy should be attempted with extreme caution and only if benefit clearly outweighs the risk.', 'Synergism', 'Dantrolene should be used with caution in patients, particularly females over 35 years of age, who are receiving concomitant estrogen therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239609/', '', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Etonogestrel, More', '', 1767369485);
