INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236871, 'Succinylcholine', 'Medroxyprogesterone acetate', 'Moderate', 'The neuromuscular blocking effect of succinylcholine may be potentiated by chronic administration of estrogens or high-dose corticosteroids.  Available data suggest that steroids may reduce plasma cholinesterase activity, resulting in decreased metabolic clearance of succinylcholine.', 'DDInter', 'Patients receiving chronic therapy with estrogens or corticosteroids should be monitored for increased or prolonged neuromuscular blockade when succinylcholine is administered.  A lower initial dosage of succinylcholine may be required.', 'Metabolism', 'Patients receiving chronic therapy with estrogens or corticosteroids should be monitored for increased or prolonged neuromuscular blockade when succinylcholine is administered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239656/', '', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Etonogestrel, More', 'Atracurium, Doxacurium, Rocuronium, Vecuronium, Tubocurarine, Mivacurium, Pancuronium, Pipecuronium, Botulinum toxin type A, Cisatracurium', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236872, 'Testolactone', 'Medroxyprogesterone acetate', 'Moderate', 'Theoretically, estrogen-containing drugs may negate the pharmacologic effects of aromatase inhibitors in the treatment of hormone-dependent breast cancer.  Aromatase inhibitors induce a state of estrogen deprivation in postmenopausal women by inhibiting the conversion of androgens to estrogens in adrenal and ovarian tissues, thus estrogen administration would be expected to reverse their therapeutic effects.', 'DDInter', 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239658/', '', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Etonogestrel, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236873, 'Thalidomide', 'Medroxyprogesterone acetate', 'Major', 'Concomitant treatment with agents that can cause thrombosis such as erythropoiesis-stimulating agents, oestrogens, selective estrogen receptor modulators, or C1 esterase inhibitors may potentiate the risk of venous thromboembolic events associated with the use of thalidomide.  When used in multiple myeloma, there is an increased risk of venous thromboembolism such as deep venous thrombosis and pulmonary embolism.  This risk increases significantly when thalidomide is combined with standard chemotherapeutic agents and/or steroids.', 'DDInter', 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving thalidomide with standard chemotherapeutic agents and/or steroids.  Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.  Prophylaxis with anticoagulants such as low-molecular weight heparins or warfarin may be appropriate, but the decision to take thromboprophylactic measures should be made after careful assessment of underlying risk factors.  If a thromboembolic event occurs during therapy with thalidomide, treatment must be discontinued and standard anticoagulation therapy initiated.  Once anticoagulation is stabilized and complications of the thromboembolic event under control, thalidomide may be restarted at the original dose if benefit is deemed to outweigh the risks.  Anticoagulation therapy should be continued during the remaining course of thalidomide treatment.', 'Synergism', 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving thalidomide with standard chemotherapeutic agents and/or steroids.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239659/', '', 'Ulipristal, Norethisterone, Norelgestromin, Norgestimate, Etynodiol, Levonorgestrel, Desogestrel, Estetrol, Etonogestrel, Norgestrel, Progesterone, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Methotrexate, Abatacept, Upadacitinib, Siponimod, Apremilast, Inebilizumab, Belumosudil, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236874, 'Thyroid, porcine', 'Medroxyprogesterone acetate', 'Moderate', 'Estrogens may increase serum thyrotropin concentration, which could be harmful in patients with thyroid cancer receiving thyroxine for thyrotropin suppression or increase dosage requirements in patients with hypothyroidism receiving thyroxine for replacement therapy.  Estrogens are known to increase serum thyroid-binding globulin concentration in a dose-dependent manner.  Consequently, there may be a reduction in unbound, or free, thyroxine available for hormone activity, which, in turn, leads to an increase in serum thyrotropin concentration.  Normally, thyroxine secretion can increase to compensate for this effect, but patients with hypothyroidism lack the mechanism to adapt.  Limited evidence suggests that transdermal estrogen therapy may not affect thyroid-binding globulin concentrations; however, more data are required to confirm that.', 'DDInter', 'In patients treated with thyroxine, serum thyrotropin should be measured approximately 12 weeks after estrogen therapy is initiated, changed or discontinued, and the thyroxine dosage adjusted accordingly.  Patients should be advised to contact their physician if clinical manifestations of hypothyroidism occur, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Distribution', 'In patients treated with thyroxine, serum thyrotropin should be measured approximately 12 weeks after estrogen therapy is initiated, changed or discontinued, and the thyroxine dosage adjusted accordingly.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239660/', '', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Etonogestrel, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236875, 'Tipranavir', 'Medroxyprogesterone acetate', 'Moderate', 'Use of tipranavir in combination with estrogens may be associated with an increased risk of skin rash.', 'DDInter', 'Women who use estrogens should be advised of a potentially increased risk of skin rash during concomitant treatment with tipranavir.  If a rash occurs, it is usually mild to moderate.  Nevertheless, patients should be instructed to contact their physician promptly, as a temporary discontinuation of tipranavir or the estrogen may be necessary.', 'Synergism', 'Women who use estrogens should be advised of a potentially increased risk of skin rash during concomitant treatment with tipranavir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239661/', '', 'Norelgestromin, Norethisterone, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Etonogestrel, Norgestrel, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236876, 'Topiramate', 'Medroxyprogesterone acetate', 'Major', 'Coadministration with topiramate, particularly at dosages above 200 mg/day, may decrease the plasma concentrations and efficacy of contraceptive hormones.  The proposed mechanism is topiramate induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of steroidal hormones.', 'DDInter', 'The potential for decreased contraceptive efficacy and increased breakthrough bleeding should be considered in patients using hormonal contraceptives (including oral, injectable, transdermal, and implantable forms) during treatment with topiramate, particularly at dosages above 200 mg/day.  Some clinicians and manufacturers recommend that patients on topiramate dosages at or above 200 mg/day who are taking oral contraceptives should receive a preparation containing no less than 30 or 35 mcg of estrogen, while others recommend at least 50 mcg.  Intrauterine systems are unlikely to be significantly affected because of their local action, thus they may be preferable to other routes of administration.  Patients should be advised to report any change in their bleeding patterns, although contraceptive efficacy can be decreased even in the absence of breakthrough bleeding.  Because use of topiramate can cause fetal harm, it is particularly important that patients not become pregnant during treatment.  Counseling should be provided regarding the use of alternative (non-hormonal) or additional (back-up) pregnancy prevention methods during and for at least 28 days after discontinuing topiramate.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Alternative, nonenzyme-inducing anticonvulsants that are not believed to interact significantly with hormonal contraceptives include clonazepam, ethosuximide, gabapentin, levetiracetam, pregabalin, tiagabine, valproic acid, vigabatrin, and zonisamide.', 'Metabolism', 'The potential for decreased contraceptive efficacy and increased breakthrough bleeding should be considered in patients using hormonal contraceptives (including oral, injectable, transdermal, and implantable forms) during treatment with topiramate, particularly at dosages above 200 mg/day.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239663/', '', 'Megestrol acetate, Estetrol, Megestrol acetate, Megestrol acetate, Diethylstilbestrol, Histrelin, Megestrol acetate, Leuprolide, Bicalutamide, Triptorelin, Goserelin', 'Ezogabine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Perampanel, Ganaxolone, Vigabatrin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236877, 'Triamcinolone', 'Medroxyprogesterone acetate', 'Moderate', 'Estrogens may enhance the systemic effects of both endogenous and exogenous corticosteroids.  The proposed mechanism is an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in a decreased rate of corticosteroid metabolic clearance.', 'DDInter', 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents.  Pharmacologic response to these agents should be monitored more closely whenever an estrogen is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Distribution', 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239664/', '', 'Ulipristal, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Desogestrel, Dienogest, Etonogestrel, Norgestrel, Megestrol acetate, More', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Ciclesonide, Mupirocin, More', 1767369485);
