INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236879, 'Trimipramine', 'Medroxyprogesterone acetate', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy.  Simultaneous TCA toxicity and reduced effects have been reported.  Akathisia has also been reported in some women taking this combination.  The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', 'DDInter', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy.  Simultaneous TCA toxicity and reduced effects have been reported.  Akathisia has also been reported in some women taking this combination.  The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.  The clinical significance of this interaction has not been established.  Monitoring for altered effects may be advisable during concomitant therapy.  Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239666/', '', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Estetrol, More', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Esketamine, Phenelzine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236880, 'Tucatinib', 'Medroxyprogesterone acetate', 'Moderate', 'Coadministration with tucatinib may increase the plasma concentrations of CYP450 3A4 substrates.  The mechanism involves tucatinib-mediated inhibition of CYP450 3A isoenzymes.', 'DDInter', 'Caution is advised when tucatinib is used concomitantly with drugs that undergo metabolism by CYP450 3A4.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever tucatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when tucatinib is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239667/', '', 'Megestrol acetate, Etynodiol, Levonorgestrel, Estetrol, Norgestrel, Megestrol acetate, Progesterone, Etynodiol, Megestrol acetate, Etynodiol, Levonorgestrel, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Ribociclib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 1767369485);
