INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236932, 'Brentuximab vedotin', 'Varicella Zoster Vaccine (Recombinant)', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition.  Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents (e.g., greater than or equal to 2 mg/kg/day or 20 mg/day of prednisone or equivalent for 14 consecutive days or more), or long-term topical or inhaled corticosteroids.', 'DDInter', 'In general, the U.S. Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.  However, higher dosages, more frequent boosters, and/or serological testing may be required in some cases.  Local guidelines and prescribing information for individual vaccines should be consulted.  For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease.  For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.  For SARS-CoV-2 (COVID-19) vaccines, vaccination should preferably be completed at least two weeks before initiation or resumption of immunosuppressive therapies; however, decisions to delay or temporarily withhold immunosuppressive therapy to complete COVID-19 vaccination should consider the individual''s risks relative to their underlying condition.  Additional shots and/or boosters may be appropriate depending on age, prior COVID-19 vaccine formulation(s) received, and other factors in individuals with moderate to severe immune compromise due to medical conditions or immunosuppressive medications or treatments (e.g., solid organ transplant recipients on immunosuppressive therapy; patients on active treatment for solid tumor and hematologic malignancies).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, the U.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/240886/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236933, 'Aldesleukin', 'Tadalafil', 'Minor', 'Based on their pharmacology, phosphodiesterase-5 (PDE5) inhibitors may conceivably potentiate the hypotensive effect of antihypertensive medications.  These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can cause peripheral vasodilation.', 'DDInter', 'Patients should be advised of the potential for interaction and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, or fainting.', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/240887/', '', 'Interferon alfa-n1, Oprelvekin, Sipuleucel-T, Elapegademase, Interferon beta-1b, Peginterferon alfa-2a, Ropeginterferon alfa-2b, Interferon alfacon-1, Peginterferon beta-1a, Interferon beta-1a, Sargramostim, More', 'Darifenacin, Vardenafil, Ammonium chloride, Apomorphine, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236934, 'Tadalafil', 'Avanafil', 'Major', 'The safety and efficacy of combining a phosphodiesterase-5 (PDE5) inhibitor with other PDE-5 inhibitors or other treatments for erectile dysfunction have not been studied.  Since these agents have systemic vasodilatory properties, concurrent use may result in additive hypotensive and other cardiovascular adverse effects.', 'DDInter', 'Concomitant use of a PDE5 inhibitor with another PDE5 inhibitor should generally be avoided.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/240892/', '', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Acetohydroxamic acid, Trospium, Mirabegron, Yohimbine, Flavoxate, Darifenacin, Phenyl salicylate, More', 'Darifenacin, Ammonium chloride, Apomorphine, Yohimbine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, Avanafil, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (236935, 'Entrectinib', 'Tadalafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors.  Dosage adjustments may be appropriate for tadalafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects.  Tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours in patients treated concomitantly with a potent CYP450 3A4 inhibitor, such as erythromycin, itraconazole, ketoconazole, protease inhibitors, and nefazodone.  Patients should be advised to promptly notify their physician if they experience potential symptoms of PDE5 inhibitor toxicity such as pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/240898/', '', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Avanafil, Phenyl salicylate, Oxybutynin, More', 1767369485);
