INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237092, 'Berotralstat', 'Trastuzumab emtansine', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', 'DDInter', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.  Clinical and laboratory monitoring are recommended following the initiation of berotralstat, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/241273/', '', 'Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237093, 'Trastuzumab emtansine', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor.', 'DDInter', 'Caution is advised when oral lefamulin is used concomitantly with drugs that are substrates of CYP450 3A4, particularly sensitive substrates or those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever oral lefamulin is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration of a moderate CYP450 3A4 inhibitor like lefamulin and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised when oral lefamulin is used concomitantly with drugs that are substrates of CYP450 3A4, particularly sensitive substrates or those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/241280/', '', 'Bacitracin, Oritavancin, Polymyxin B, Daptomycin, Vancomycin, Telavancin, Methenamine, Spectinomycin, Fosfomycin, Tedizolid, Dalbavancin', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237094, 'Trastuzumab emtansine', 'Osilodrostat', 'Moderate', 'Coadministration with osilodrostat may increase the plasma concentrations of drugs that are metabolized by CYP450 1A2, 2C19, 2D6, and/or 3A4.  Osilodrostat has been shown to be a moderate inhibitor of CYP450 1A2, a mild to borderline moderate inhibitor of CYP450 2C19, and a weak inhibitor of CYP450 2D6 and 3A4.', 'DDInter', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osilodrostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/241287/', '', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237095, 'Trastuzumab emtansine', 'Selinexor', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect.', 'DDInter', 'Caution is advised during concomitant use of agents with neurotoxic effects.  Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.  Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded.  Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.  If feasible, therapy should generally be reinstituted only after resolution of neuropathy symptoms or return of symptoms to baseline status.  In some cases, permanent dosage reductions may be required.', 'Synergism', 'Caution is advised during concomitant use of agents with neurotoxic effects.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/241290/', '', 'Pertuzumab, Olaparib, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Sotorasib, Enasidenib, Mitotane, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237096, 'Trastuzumab emtansine', 'Selpercatinib', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  The mechanism is reduced clearance due to inhibition of CYP450 3A4 by selpercatinib.  The interaction may be particularly important for sensitive substrates or those that demonstrate a narrow therapeutic index.', 'DDInter', 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/241291/', '', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Dacomitinib, Tepotinib, Fedratinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237097, 'Epinephrine', 'Ketamine', 'Moderate', 'Ketamine can cause hemodynamic instability such as arrhythmias and transient increases or decreases in blood pressure, heart rate, and cardiac index.  Coadministration of ketamine with direct or indirect acting sympathomimetics or vasopressin may enhance the sympathomimetic effects of ketamine leading to additive increases in blood pressure and heart rate.', 'DDInter', 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.  Dosage adjustments should be considered according to the patient''s clinical situation.  Some authorities suggest avoiding the use of ketamine in patients receiving drugs with a hypertensive effect such as ergometrine.', 'Synergism', 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/241298/', '', 'Tetracycline, Minocycline, Miconazole, Doxycycline, Hydrocortisone, Metronidazole, Clotrimazole, Prednisolone, Triamcinolone, Acetylsalicylic acid, Chlorhexidine, More', 'Thiopental, Alfentanil, Remifentanil, Sodium oxybate, Methohexital, Nitrous oxide, Esketamine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237098, 'Iobenguane (I-123)', 'Amlodipine', 'Moderate', 'Coadministration with drugs that block norepinephrine uptake or deplete norepinephrine stores may decrease iobenguane I-123 uptake in neuroendocrine tumors and lead to false-negative imaging results.', 'DDInter', 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.  Patients should be monitored for the occurrence of clinically significant withdrawal symptoms, especially patients with elevated levels of circulating catecholamines and their metabolites.', 'Distribution', 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/241302/', '', 'Hydrochlorothiazide, Aliskiren, Ramipril, Valsartan, Simvastatin, Atorvastatin, Lovastatin, Rosuvastatin, Lisinopril, Perindopril, Niacin, More', '', 1767369485);
