INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237199, 'Dexmethylphenidate', 'Spironolactone', 'Moderate', 'Methylphenidate and other derivative amphetamines may decrease the therapeutic effects of antihypertensive drugs.', 'DDInter', 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.  The dosage of the antihypertensive drug should be adjusted as needed.', 'Antagonism', 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/242161/', '', 'Amphetamine, Pemoline, Metamfetamine, Lisdexamfetamine, Solriamfetol, Caffeine, Dextroamphetamine, Armodafinil, Citicoline', 'Finerenone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237200, 'Hydralazine', 'Spironolactone', 'Moderate', 'Concomitant treatment with other antihypertensive agents or vasodilators, including alpha-adrenoreceptor antagonists, angiotensin converting enzyme (ACE) inhibitors, angiotensin receptor blockers (ARBs), beta-adrenergic blockers, calcium channel blockers, diuretics and nitrates, may potentiate the hypotensive effects of hydralazine and dihydralazine.', 'DDInter', 'Blood pressure and heart rate should be closely monitored when hydralazine or dihydralazine is used with other agents that can induce hypotension.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/242166/', '', 'Nitroprusside, Diazoxide, Prazosin, Rauwolfia serpentina root, Reserpine, Guanethidine, Deserpidine', 'Amiloride, Triamterene', 1767369485);
