INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237400, 'Alpelisib', 'Tivozanib', 'Moderate', 'Coadministration of inhibitors of the breast cancer resistance protein (BCRP) transporter with alpelisib may increase the plasma concentrations and risk of adverse effects of alpelisib, which is a substrate of this efflux transporter.', 'DDInter', 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia.  Some authorities recommend coadministration of alpelisib with BCRP inhibitors be avoided whenever possible (US).', 'Excretion', 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243202/', '', 'Entrectinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Bosutinib, Selumetinib, More', 'Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Fedratinib, Avapritinib, Selumetinib, Vandetanib, Capmatinib, Duvelisib, Zanubrutinib, More', 1767369485);
