INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237597, 'Heparin (flush)', 'Vorapaxar', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', 'DDInter', 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', 'Caution and monitoring (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245156/', '', 'Clopidogrel, Protein C, Acenocoumarol, Selexipag, Vorapaxar, Acetylsalicylic acid, Ascorbic acid, Cyclosporine, Sirolimus, Potassium Iodide, Vitamin A, More', 'Acenocoumarol, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237598, 'Vorapaxar', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245167/', '', 'Entrectinib, Imatinib, Dabrafenib, Asciminib, Dacomitinib, Tepotinib, Bosutinib, Vemurafenib, Lapatinib, Ribociclib, Vandetanib, More', 'Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237599, 'Chloramphenicol', 'Vorinostat', 'Moderate', 'Coadministration of chloramphenicol with other agents that can cause bone marrow depression or aplastic anemia may increase the risk and/or severity of chloramphenicol hematologic toxicity.', 'DDInter', 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245213/', '', 'Tetracycline, Mupirocin, Rifamycin, Oxytetracycline, Bacitracin, Demeclocycline, Rifaximin, Gentamicin, Amikacin, Neomycin, Tetracycline, More', 'Pertuzumab, Vismodegib, Sotorasib, Enasidenib, Ivosidenib, Talimogene laherparepvec, Mitotane, Porfimer sodium, Estramustine, Celecoxib, Aminolevulinic acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237600, 'Clozapine', 'Vorinostat', 'Major', 'Coadministration of clozapine with other agents that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity.', 'DDInter', 'Caution and close monitoring are advised when clozapine is used with other agents that have a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function.  Agents that may be significantly myelotoxic include antineoplastic drugs, some anticonvulsant and antirheumatic medications, albendazole, chloramphenicol, colchicine, dapsone, interferons, linezolid, pentamidine, procainamide, and zidovudine.', 'Synergism', 'Caution and close monitoring are advised when clozapine is used with other agents that have a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245214/', '', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Quetiapine, Prochlorperazine, Promazine, Thioridazine, Lumateperone, Pimozide, More', 'Pertuzumab, Vismodegib, Sotorasib, Enasidenib, Belzutifan, Talimogene laherparepvec, Sonidegib, Estramustine, Aminolevulinic acid', 1767369485);
