INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237619, 'Sibutramine', 'Pazopanib', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme.', 'DDInter', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme.  Inhibitors such as ketoconazole, cimetidine, and erythromycin have caused moderate increases in the peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of the active metabolites.  The data suggest that although the potential for such interaction exists, the magnitude appears to be small.  No special precaution appears necessary; however, dosage adjustments as well as clinical and laboratory monitoring may be appropriate in some cases when a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245434/', '', 'Dacomitinib, Tepotinib, Bosutinib, Vemurafenib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Afatinib, Tivozanib, Brigatinib, More', 'Dexfenfluramine, Ephedrine, Fenfluramine, Bupropion, Phentermine, Orlistat, Lorcaserin, Diethylpropion, Mazindol', 1767369485);
