INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (238414, 'Drospirenone', 'Pasireotide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of drospirenone, which is partially metabolized by the isoenzyme.', 'DDInter', 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.g., renal insufficiency; coadministration with medications that can increase potassium such as aldosterone antagonists, angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium-sparing diuretics, potassium supplements, and heparin).  Consider checking serum potassium levels during the first treatment cycle and periodically thereafter as indicated.', 'Metabolism', 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/248003/', '', 'Ulipristal, Norethisterone, Norelgestromin, Etonogestrel, Megestrol acetate, Norgestimate, Etynodiol, Estradiol, Levonorgestrel, Medroxyprogesterone acetate, Desogestrel, More', 'Norethisterone, Nafarelin, Cetrorelix, Estradiol, Ganirelix, Relugolix, Gonadorelin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (238415, 'Indium In-111 pentetreotide', 'Pasireotide', 'Moderate', 'Somatostatin analogs may reduce the accumulation of indium In-111 pentetreotide radioactivity in gastro-entero-neuroendocrine tumors and carcinoid tumors.', 'DDInter', 'Consideration should be given to temporarily suspending somatostatin analog therapy prior to the administration of indium In-111 pentetreotide.  If temporary withdrawal of the somatostatin analog is considered possible, some authorities propose a withdrawal period of three days prior to administration of indium In-111 pentetreotide.  Since withdrawal of the somatostatin analog may lead to rebound effects such as hypoglycemia (particularly in patients with insulinoma and carcinoid syndrome), patients should be monitored for any signs of withdrawal.', 'Distribution', 'Consideration should be given to temporarily suspending somatostatin analog therapy prior to the administration of indium In-111 pentetreotide.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/248008/', '', 'Capromab pendetide, Gallium Ga-68 gozetotide, Fludeoxyglucose (18F)', 'Norethisterone, Nafarelin, Elagolix, Cetrorelix, Estradiol, Ganirelix, Relugolix, Gonadorelin', 1767369485);
