INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (238748, 'Amisulpride', 'Remimazolam', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.  Cautious dosage titration may be required, particularly at treatment initiation.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/250112/', '', 'Pimavanserin', 'Suvorexant, Triazolam, Flurazepam, Methohexital, Methylphenobarbital, Zolpidem, Pentobarbital, Zaleplon, Dichloralphenazone, Valerian, Temazepam, More', 1767369485);
