INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (238849, 'Darolutamide', 'Clomipramine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.  Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/250490/', '', 'Citalopram, Maprotiline, Vortioxetine, Milnacipran, Levomilnacipran, Duloxetine, Isocarboxazid, Amoxapine, Esketamine, Phenelzine, Desipramine, More', 'Anastrozole, Fulvestrant, Letrozole, Aminoglutethimide, Exemestane', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (238850, 'Eletriptan', 'Clomipramine', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors, monoamine oxidase inhibitors, tricyclic antidepressants, 5-HT1 receptor agonists, ergot alkaloids, cyclobenzaprine, lithium, St. John''s wort, phenylpiperidine opioids, dextromethorphan, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', 'DDInter', 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.  Patients should be closely monitored for symptoms of the serotonin syndrome during treatment.  Particular caution is advised when increasing the dosages of these agents.  The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life.  For example, some experts suggest a 5-week washout period following use of fluoxetine and 3 weeks following the use of vortioxetine before administering another serotonergic agent.  Individual product labeling for washout periods should be consulted for current recommendations.  If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.  Moderately ill patients may also benefit from the administration of a serotonin antagonist (e.g., cyproheptadine, chlorpromazine).  Severe cases should be managed under consultation with a toxicologist and may require sedation, neuromuscular paralysis, intubation, and mechanical ventilation in addition to the other measures.', 'Synergism', 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/250493/', '', 'Esketamine', 'Rimegepant, Eptinezumab, Ubrogepant, Galcanezumab, Atogepant, Methysergide, Erenumab, Fremanezumab', 1767369485);
