INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (238899, 'Pralsetinib', 'Meloxicam', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/250886/', '', 'Chloroprocaine, Tetracaine, Mepivacaine, Procaine, Prilocaine, Articaine, Etidocaine, Fenoprofen, Meclofenamic acid, Ketorolac, Phenylbutazone, More', 'Entrectinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, Ribociclib, Vandetanib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (238900, 'Turmeric', 'Meloxicam', 'Moderate', 'Turmeric may potentiate the bleeding risk associated with drugs that interfere with platelet function or coagulation.', 'DDInter', 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.  Clinical and laboratory observation for hematologic complications is recommended.', 'Synergism', 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/250891/', '', 'Ropivacaine, Lidocaine, Chloroprocaine, Levobupivacaine, Bupivacaine, Tetracaine, Mepivacaine, Procaine, Prilocaine, Articaine, Etidocaine, More', '', 1767369485);
