INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (238987, 'Ulipristal', 'Apalutamide', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ulipristal acetate and its pharmacologically active metabolite.  Based on in vitro and pharmacokinetic data, ulipristal acetate is thought to be primarily metabolized by CYP450 3A4 to mono-demethylated and di-demethylated metabolites.', 'DDInter', 'Concomitant use of ulipristal acetate with potent and moderate CYP450 3A4 inducers should generally be avoided due to the potential for loss of therapeutic efficacy.  For patients who have used enzyme-inducing drugs within the past 4 weeks and are seeking emergency contraception, ulipristal acetate is not recommended and a non-hormonal method (i.e.  a copper intrauterine device (Cu-IUD)) should be considered.', 'Metabolism', 'Concomitant use of ulipristal acetate with potent and moderate CYP450 3A4 inducers should generally be avoided due to the potential for loss of therapeutic efficacy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251343/', '', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Bicalutamide, Degarelix, Nilutamide, More', 'Estetrol, Raloxifene, Prasterone, Danazol', 1767369485);
