INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239015, 'Bupropion', 'Larotrectinib', 'Moderate', 'Coadministration with inducers of CYP450 2B6 may decrease the plasma concentrations of bupropion.  In vitro findings suggest that CYP450 2B6 is the principal isoenzyme involved in the formation of hydroxybupropion, one of three active metabolites of bupropion.', 'DDInter', 'Pharmacologic response to bupropion should be monitored more closely whenever a CYP450 2B6 inducer is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary.  Concomitant use of potent and moderate CYP450 2B6 inducers such as carbamazepine, efavirenz, rifampin, ritonavir, and lopinavir-ritonavir should be avoided with fixed-dose combination products such as bupropion-naltrexone or bupropion-dextromethorphan.  For other bupropion products, increases in bupropion dosage should be guided by clinical response; however, the maximum recommended dosage should not be exceeded.', 'Metabolism', 'Pharmacologic response to bupropion should be monitored more closely whenever a CYP450 2B6 inducer is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251440/', '', 'Dexfenfluramine, Ephedrine, Fenfluramine, Phentermine, Orlistat, Lorcaserin, Diethylpropion, Mazindol, Maprotiline, Fluoxetine, Milnacipran, More', 'Entrectinib, Pralsetinib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Bosutinib, Vemurafenib, More', 1767369485);
