INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239067, 'Prednisone', 'Axicabtagene ciloleucel', 'Major', 'Prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy may interfere with the therapeutic effects of such drugs as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel.  The mechanism for this interaction has not been delineated.  Administration of systemic corticosteroids in an effort to manage the toxicities associated with these drugs does not affect the expansion and persistence of CAR T cells.', 'DDInter', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.  However, systemic corticosteroids may be used for the management of adverse effects associated with these drugs, such as cytokine release syndrome and neurological-related toxicities, without affecting the expansion and persistence of CAR T cells.', 'Others', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251677/', '', 'Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Niraparib, Porfimer sodium, More', 'Cromoglicic acid, Olsalazine, Sulfasalazine, Balsalazide, Beclomethasone dipropionate, Mesalazine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239068, 'Bacillus calmette-guerin substrain tice live antigen', 'Prednisone', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251678/', '', 'Cromoglicic acid, Olsalazine, Sulfasalazine, Balsalazide, Beclomethasone dipropionate, Mesalazine', '', 1767369485);
