INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239070, 'Enzalutamide', 'Prednisone', 'Moderate', 'Coadministration with enzalutamide may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-glycoprotein (P-gp) efflux transporter.  The proposed mechanism involves accelerated clearance via these routes due to enzalutamide-mediated induction.', 'DDInter', 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enzalutamide is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251692/', '', 'Darolutamide, Toremifene, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Abiraterone, Exemestane', 'Cromoglicic acid, Olsalazine, Sulfasalazine, Balsalazide, Beclomethasone dipropionate, Mesalazine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239071, 'Prednisone', 'Lisocabtagene maraleucel', 'Major', 'Prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy may interfere with the therapeutic effects of such drugs as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel.  The mechanism for this interaction has not been delineated.  Administration of systemic corticosteroids in an effort to manage the toxicities associated with these drugs does not affect the expansion and persistence of CAR T cells.', 'DDInter', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.  However, systemic corticosteroids may be used for the management of adverse effects associated with these drugs, such as cytokine release syndrome and neurological-related toxicities, without affecting the expansion and persistence of CAR T cells.', 'Others', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251708/', '', 'Cromoglicic acid, Olsalazine, Sulfasalazine, Balsalazide, Mesalazine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239072, 'Prednisone', 'Osilodrostat', 'Moderate', 'Coadministration with osilodrostat may increase the plasma concentrations of drugs that are metabolized by CYP450 1A2, 2C19, 2D6, and/or 3A4.  Osilodrostat has been shown to be a moderate inhibitor of CYP450 1A2, a mild to borderline moderate inhibitor of CYP450 2C19, and a weak inhibitor of CYP450 2D6 and 3A4.', 'DDInter', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osilodrostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251720/', '', 'Cromoglicic acid, Olsalazine, Sulfasalazine, Balsalazide, Beclomethasone dipropionate, Mesalazine', '', 1767369485);
