INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239387, 'Vemurafenib', 'Morphine', 'Major', 'Coadministration with P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of morphine and its risk of adverse effects, including hypotension, respiratory and CNS depression, profound sedation, coma, and death.  The proposed mechanism may involve inhibition of the intestinal P-gp efflux transporter, resulting in enhanced oral bioavailability of morphine, a P-gp substrate.  The interaction may be more significant for orally administered morphine.  According to some authorities, the concomitant use of a P-gp inhibitor with oral morphine may increase the systemic exposure of morphine by approximately 2-fold.', 'DDInter', 'Caution is recommended whenever morphine, particularly orally administered morphine, is used concomitantly with a P-gp inhibitor.  Close clinical and laboratory monitoring should be considered whenever a P-gp inhibitor is added to or withdrawn from therapy, and the morphine dosage adjusted as necessary.  Patients should be monitored closely for signs and symptoms of respiratory depression and sedation that may be greater than otherwise expected.  In addition, patients should be advised to avoid driving or operating hazardous machinery until they know how these medications affect them.', 'Absorption', 'Caution is recommended whenever morphine, particularly orally administered morphine, is used concomitantly with a P-gp inhibitor.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/253551/', '', 'Opium, Eluxadoline, Diphenoxylate, Difenoxin, Oxymorphone, Tapentadol, Meperidine, Hydromorphone, Dezocine, Opium, Ibuprofen, More', 'Tinidazole, Ascorbic acid, Lactic acid, Methazolamide, Trichlormethiazide, Ciclopirox, Darunavir, Tioconazole, Brinzolamide, Diiodohydroxyquinoline, Oxytetracycline, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239388, 'Venetoclax', 'Morphine', 'Moderate', 'Coadministration with venetoclax may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp).  The proposed mechanism is decreased clearance due to inhibition of P-glycoprotein-mediated drug efflux in the intestine, liver, and/or kidney by venetoclax.', 'DDInter', 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.  Alternatives should be considered whenever possible.  The manufacturer recommends that drugs sensitive to P-gp inhibition in the gastrointestinal tract be administered at least 6 hours before venetoclax.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate following the initiation or discontinuation of venetoclax.', 'Metabolism, Absorption, Excretion', 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/253552/', '', 'Opium, Eluxadoline, Diphenoxylate, Difenoxin, Naltrexone, Oxymorphone, Oxycodone, Methadone, Pentazocine, Buprenorphine, Oliceridine, More', 'Pertuzumab, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Belzutifan, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239389, 'Bacillus calmette-guerin substrain tice live antigen', 'Ofatumumab', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/253555/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', '', 1767369485);
