INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239455, 'Bromocriptine', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of bromocriptine.  Orally administered bromocriptine is extensively metabolized in the gastrointestinal tract and liver by CYP450 3A4, with approximately 93% of the absorbed dose undergoing first-pass metabolism and only the remaining 7% reaching systemic circulation.', 'DDInter', 'Concomitant use of bromocriptine with potent CYP450 3A4 inhibitors should be avoided when possible.  In patients who have been treated with a potent CYP450 3A4 inhibitor, an adequate washout of the inhibitor (approximately 3 to 5 elimination half-lives) is recommended prior to initiation of bromocriptine.  If coadministration with a potent CYP450 3A4 inhibitor is required, bromocriptine dosage should be reduced to avoid toxicity.  Caution and close monitoring for development of adverse effects are advisable, and the bromocriptine dosage further adjusted if necessary.', 'Metabolism', 'Concomitant use of bromocriptine with potent CYP450 3A4 inhibitors should be avoided when possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/254700/', '', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Valaciclovir, Rilpivirine, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Apomorphine, Opicapone, Cabergoline, Amantadine, Rasagiline, Entacapone, Selegiline, Tolcapone, Rotigotine, Pergolide, Pramipexole, More', 1767369485);
