INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239574, 'Buprenorphine', 'Selegiline', 'Major', 'Coadministration of monoamine oxidase inhibitors (MAOIs), such as selegiline (an MAO-B inhibitor), with opioid analgesics may result in serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors.', 'DDInter', 'Concomitant use of MAOIs, such as selegiline, with opioid analgesics should generally be avoided.  At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with an opioid analgesic.  If coadministration is unavoidable, patients should be monitored for signs and symptoms of serotonin syndrome which may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.  Some authorities consider the use of selegiline with opioid analgesics to be contraindicated (UK).', 'Synergism', 'Concomitant use of MAOIs, such as selegiline, with opioid analgesics should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256363/', '', 'Acamprosate, Naltrexone, Varenicline, Nalmefene, Nicotine, Naloxone, Naltrexone, Celecoxib', 'Safinamide, Amantadine, Cabergoline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239575, 'Buprenorphine', 'Tolevamer', 'Moderate', 'Coadministration with medications that can cause constipation such as opioids may increase the risk of intestinal injuries associated with the use of sodium polystyrene sulfonate.', 'DDInter', 'Because opioids commonly cause constipation, caution is advised when used during treatment with sodium polystyrene sulfonate.  The prescribing information recommends avoiding the use of sodium polystyrene sulfonate in patients who are at risk for developing constipation or impaction, including those with a history of impaction, chronic constipation, inflammatory bowel disease, ischemic colitis, vascular intestinal atherosclerosis, previous bowel resection, or bowel obstruction, as well as those who have not had a bowel movement post-surgery.  If clinically significant constipation develops, treatment with sodium polystyrene sulfonate should be discontinued until normal bowel motion is resumed.  Concomitant administration of sorbitol is not recommended.', 'Synergism', 'Because opioids commonly cause constipation, caution is advised when used during treatment with sodium polystyrene sulfonate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256365/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Ibuprofen, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Deferoxamine, Levoleucovorin, Arginine, Methionine, More', 1767369485);
