INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239641, 'Hydroxychloroquine', 'Berotralstat', 'Moderate', 'Coadministration with moderate or strong inhibitors of CYP450 2C8 or CYP450 3A4 may increase the plasma concentration of hydroxychloroquine and the risk of toxicities such as QT interval prolongation and ventricular arrhythmias.  Following coadministration with cimetidine, a weak to moderate CYP450 3A4 inhibitor, a 2-fold increase in chloroquine exposure occurred.  Because chloroquine and hydroxychloroquine have similar structures and metabolic elimination pathways, a similar interaction could be observed for hydroxychloroquine.', 'DDInter', 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.  Clinical and laboratory monitoring should be considered whenever a moderate or strong CYP450 3A4 or 2C8 inhibitor is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256824/', '', 'Hemin, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 'Lumefantrine, Pyrimethamine, Tafenoquine, Atovaquone, Primaquine, Proguanil, Sulfadoxine', 1767369485);
