INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239697, 'Bromocriptine', 'Bexarotene', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bromocriptine.', 'DDInter', 'The potential for diminished pharmacologic effects of bromocriptine should be considered during coadministration with CYP450 3A4 inducers.  Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of bromocriptine should be considered during coadministration with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/257779/', '', 'Pertuzumab, Olaparib, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Apomorphine, Ropinirole, Opicapone, Cabergoline, Amantadine, Rasagiline, Entacapone, Selegiline, Tolcapone, Rotigotine, Pergolide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239698, 'Budesonide', 'Bexarotene', 'Moderate', 'Concomitant use of orally and rectally administered formulations of budesonide with inducers of CYP450 3A4 may decrease budesonide systemic as well as local exposure at the gut mucosa.  Budesonide undergoes extensive first-pass and systemic metabolism via intestinal and hepatic CYP450 3A4', 'DDInter', 'The potential for diminished pharmacologic effects of orally and rectally administered budesonide should be considered during concomitant treatment with drugs that induce CYP450 3A4.  Alternative treatments or budesonide dose adjustments may be required if an interaction is suspected.', 'Metabolism, Absorption', 'The potential for diminished pharmacologic effects of orally and rectally administered budesonide should be considered during concomitant treatment with drugs that induce CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/257780/', '', 'Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Porfimer sodium, Niraparib, Selinexor, More', 'Formoterol, Cromoglicic acid, Ipratropium, Isoprenaline, Tiotropium, Beclomethasone dipropionate, Terbutaline, Aclidinium, Glycopyrronium, Mometasone, Fluticasone, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239699, 'Bexarotene', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/257826/', '', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Enasidenib, Porfimer sodium, Aflibercept, Trastuzumab, Hydroxyurea, More', 'Futibatinib, Regorafenib, Lenvatinib, Erdafitinib, Neratinib, Trametinib, Vandetanib, Nintedanib, Afatinib, Dacomitinib, Tivozanib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239700, 'Bexarotene', 'Tramadol', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tramadol, which is primarily metabolized by CYP450 3A4 and 2D6.  Reduced opioid efficacy can occur, including opioid withdrawal in physically dependent patients.', 'DDInter', 'Caution is advised when tramadol is used with CYP450 3A4 inducers.  Patients should be monitored for opioid efficacy and signs of withdrawal.  If a CYP450 3A4 inducer is discontinued, monitor for seizures, serotonin syndrome, sedation, and respiratory depression.', 'Metabolism', 'Caution is advised when tramadol is used with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/257855/', '', 'Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, More', 'Codeine, Morphine, Oxymorphone, Tapentadol, Meperidine, Hydromorphone, Acetaminophen, Dezocine, Opium, Dextropropoxyphene, Ibuprofen, More', 1767369485);
