INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240153, 'Beclomethasone dipropionate', 'Lisocabtagene maraleucel', 'Major', 'Prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy may interfere with the therapeutic effects of such drugs as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel.  The mechanism for this interaction has not been delineated.  Administration of systemic corticosteroids in an effort to manage the toxicities associated with these drugs does not affect the expansion and persistence of CAR T cells.', 'DDInter', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.  However, systemic corticosteroids may be used for the management of adverse effects associated with these drugs, such as cytokine release syndrome and neurological-related toxicities, without affecting the expansion and persistence of CAR T cells.', 'Others', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/259778/', '', 'Fluocinolone acetonide, Clobetasol, Fluocinonide, Fluorometholone, Formoterol, Cromoglicic acid, Ipratropium, Isoprenaline, Tiot