INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240198, 'Heparin (flush)', 'Ticagrelor', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', 'DDInter', 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', 'Caution and monitoring (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/259890/', '', 'Ticagrelor, Protein C, Acenocoumarol, Selexipag, Cangrelor, Ascorbic acid, Vitamin A, Potassium Iodide, Acetylcysteine, Nandrolone', 'Acenocoumarol, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240199, 'Ticagrelor', 'Lumateperone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lumateperone.  In vitro studies show that multiple enzymes are involved in the metabolism of lumateperone, including but not limited to uridine 5''-diphospho-glucuronosyltransferases (UGT) 1A1, 1A4, and 2B15; aldoketoreductase (AKR) 1C1, 1B10, and 1C4; and cytochrome P450 (CYP450) 3A4, 2C8, and 1A2.', 'DDInter', 'Caution is advised when lumateperone is used with CYP450 3A4 inhibitors.  Patients should be monitored for increased adverse effects such as extrapyramidal symptoms, tardive dyskinesia, hyperglycemia, dyslipidemia, hyperprolactinemia, neutropenia, leukopenia, orthostatic hypotension, syncope, seizures, cognitive and motor impairment, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Caution is advised when lumateperone is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/259897/', '', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Quetiapine, Prochlorperazine, Promazine, Thioridazine, Trifluoperazine, Paliperidone, More', 'Betrixaban, Clopidogrel, Cilostazol, Apixaban, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 1767369485);
