INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240497, 'Ginkgo biloba', 'Diclofenac', 'Moderate', 'Ginkgo may potentiate the risk of bleeding associated with anticoagulants, platelet inhibitors, nonsteroidal anti-inflammatory drugs (NSAIDs), and thrombolytic agents.  Ginkgolide B, a component of ginkgo, inhibits platelet-activating factor by displacing it from its receptor-binding site, resulting in reduced platelet aggregation.', 'DDInter', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.  In general, consumption of ginkgo should be avoided during use of coagulation-modifying agents and at least two weeks prior to surgery.  In patients who have used this herb extensively prior to receiving anticoagulation, antiplatelet, NSAID or thrombolytic therapy, the potential for an interaction should be considered.  Close clinical and laboratory observation for hematologic complications is recommended.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261297/', '', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Salicylic acid, Fluocinolone acetonide, Cortisone, More', 'Memantine, Tacrine, Aducanumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240498, 'Heparin (flush)', 'Diclofenac', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', 'DDInter', 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', 'Caution and monitoring (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261298/', '', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Salicylic acid, Fluocinolone acetonide, Cortisone, More', 'Protein C, Selexipag, Pentosan polysulfate, Vitamin A, Ascorbic acid, Nandrolone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240499, 'Diclofenac', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261315/', '', 'Fluocinolone acetonide, Fluorometholone, Salicylic acid, Ketorolac, Fluocinolone acetonide, Fluorometholone, Bromfenac, Fenoprofen, Meclofenamic acid, Ketorolac, Phenylbutazone, More', 'Entrectinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Larotrectinib, Bosutinib, Vemurafenib, Selumetinib, Ribociclib, Vandetanib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240500, 'Diclofenac', 'Turmeric', 'Moderate', 'Turmeric may potentiate the bleeding risk associated with drugs that interfere with platelet function or coagulation.', 'DDInter', 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.  Clinical and laboratory observation for hematologic complications is recommended.', 'Synergism', 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261320/', '', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Salicylic acid, Fluocinolone acetonide, Cortisone, More', '', 1767369485);
