INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240592, 'Dasatinib', 'Lumateperone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lumateperone.  In vitro studies show that multiple enzymes are involved in the metabolism of lumateperone, including but not limited to uridine 5''-diphospho-glucuronosyltransferases (UGT) 1A1, 1A4, and 2B15; aldoketoreductase (AKR) 1C1, 1B10, and 1C4; and cytochrome P450 (CYP450) 3A4, 2C8, and 1A2.', 'DDInter', 'Caution is advised when lumateperone is used with CYP450 3A4 inhibitors.  Patients should be monitored for increased adverse effects such as extrapyramidal symptoms, tardive dyskinesia, hyperglycemia, dyslipidemia, hyperprolactinemia, neutropenia, leukopenia, orthostatic hypotension, syncope, seizures, cognitive and motor impairment, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Caution is advised when lumateperone is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/262036/', '', 'Acalabrutinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, More', 'Brexpiprazole, Loxapine, Molindone, Thiothixene', 1767369485);
