INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240601, 'Capmatinib', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor.', 'DDInter', 'Caution is advised when oral lefamulin is used concomitantly with drugs that are substrates of CYP450 3A4, particularly sensitive substrates or those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever oral lefamulin is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration of a moderate CYP450 3A4 inhibitor like lefamulin and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised when oral lefamulin is used concomitantly with drugs that are substrates of CYP450 3A4, particularly sensitive substrates or those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/262072/', '', 'Futibatinib, Copanlisib, Dacomitinib, Erdafitinib, Abemaciclib, Trametinib, Afatinib, Duvelisib, Tivozanib, Alectinib, Binimetinib', 'Tinidazole, Linezolid, Bacitracin, Polymyxin B, Daptomycin, Vancomycin, Metronidazole, Telavancin, Methenamine, Spectinomycin, Nitrofurantoin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240602, 'Capmatinib', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/262081/', '', 'Copanlisib, Abemaciclib, Erdafitinib, Neratinib, Erlotinib, Pralsetinib, Dacomitinib, Alectinib, Binimetinib', 'Entrectinib, Asciminib, Acalabrutinib, Dacomitinib, Avapritinib, Selumetinib, Vandetanib, Capmatinib, Ibrutinib, Tivozanib, Zanubrutinib, More', 1767369485);
