INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240642, 'Osilodrostat', 'Nicardipine', 'Moderate', 'Coadministration with osilodrostat may increase the plasma concentrations of drugs that are metabolized by CYP450 1A2, 2C19, 2D6, and/or 3A4.  Osilodrostat has been shown to be a moderate inhibitor of CYP450 1A2, a mild to borderline moderate inhibitor of CYP450 2C19, and a weak inhibitor of CYP450 2D6 and 3A4.', 'DDInter', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osilodrostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/262321/', '', 'Clevidipine, Celecoxib', '', 1767369485);
