INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240902, 'Hemin', 'Streptokinase', 'Moderate', 'Theoretically, hemin may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 'The use of hemin with concurrent anticoagulant therapy should be avoided.  Caution is advised if hemin is used in combination with other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.  Close clinical and laboratory observation for hematologic complications is recommended.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'The use of hemin with concurrent anticoagulant therapy should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263534/', '', 'Voxelotor, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Berotralstat, Lanadelumab, Icatibant, Crizanlizumab', 'Protein C, Acenocoumarol, Selexipag, Desirudin, Hemin, Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Human C1-esterase inhibitor, Berotralstat, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240903, 'Heparin (flush)', 'Streptokinase', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', 'DDInter', 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', 'Caution and monitoring (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263535/', '', 'Alteplase, Streptokinase, Antithrombin III human, Tenecteplase, Acenocoumarol, Selexipag, Urokinase, Anistreplase, Reteplase, Alteplase, Ascorbic acid, More', 'Acenocoumarol, Selexipag, Hemin, Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Human C1-esterase inhibitor, Berotralstat, Lanadelumab, Icatibant, Crizanlizumab', 1767369485);
