INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240908, 'Tositumomab', 'Streptokinase', 'Major', 'Coadministration of tositumomab and iodine I 131 tositumomab with drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.  Close clinical and laboratory observation for bleeding complications is recommended during and after the therapeutic regimen.  A complete blood count (CBC) with differential and platelet count should be obtained prior to and at least weekly for a minimum of 10 weeks after treatment.', 'Synergism', 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263547/', '', 'Dicoumarol, Desirudin, Protein C, Acenocoumarol, Selexipag, Anistreplase, Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Human C1-esterase inhibitor, More', 'Iodide I-131', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (240909, 'Botulinum toxin type A', 'Streptomycin', 'Major', 'The effects of botulinum toxin may be potentiated by neuromuscular blockers, aminoglycoside antibiotics, and other drugs that can interfere with neuromuscular transmission.  Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive neuromuscular blocking effects may occur when these agents are used together.', 'DDInter', 'Caution is advised when botulinum toxin is used in combination with neuromuscular blockers, aminoglycoside antibiotics, or other drugs that can interfere with neuromuscular transmission.  These drugs should be avoided in patients suspected of having botulinum toxin overdose or poisoning.  Because the effects of botulinum toxin products may spread from the area of injection, patients should be monitored closely for adverse effects such as asthenia, generalized muscle weakness, diplopia, blurred vision, ptosis, dysphagia, dysphonia, dysarthria, urinary incontinence and breathing difficulties, which can occur hours to weeks after injection.  Swallowing and breathing difficulties may be life-threatening or fatal.  Patients with peripheral motor neuropathic diseases, amyotrophic lateral sclerosis, or neuromuscular junctional disorders such as myasthenia gravis or Lambert-Eaton syndrome may be particularly susceptible.', 'Synergism', 'Caution is advised when botulinum toxin is used in combination with neuromuscular blockers, aminoglycoside antibiotics, or other drugs that can interfere with neuromuscular transmission.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263553/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Rifapent