INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241477, 'Denosumab', 'Cinacalcet', 'Moderate', 'Coadministration of denosumab with calcimimetic drugs such as cinacalcet and etelcalcetide, may increase the risk of hypocalcemia.  This risk may be significantly higher in patients with severe renal dysfunction (creatinine clearance less than 30 mL/min) and in those receiving dialysis.  The mechanism of this interaction is thought to be due to additive hypocalcemic effects.', 'DDInter', 'Caution and close monitoring of serum calcium concentrations are recommended when denosumab is used concomitantly with calcimimetic drugs such as cinacalcet and etelcalcetide.  Patients and clinicians should be alert for signs and symptoms of hypocalcemia, such as numbness or tingling in the fingers, toes, or around the mouth, muscle spasms, twitches or cramps, seizures, mental status changes (confusion, irritability, depression), and irregular heartbeats.', 'Synergism', 'Caution and close monitoring of serum calcium concentrations are recommended when denosumab is used concomitantly with calcimimetic drugs such as cinacalcet and etelcalcetide.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267782/', '', 'Paricalcitol, Doxercalciferol, Salmon calcitonin, Calcifediol', 'Tiludronic acid, Vosoritide, Etidronic acid, Ibandronate, Cholecalciferol, Romosozumab, Pamidronic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241478, 'Enzalutamide', 'Cinacalcet', 'Moderate', 'Coadministration with enzalutamide may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-glycoprotein (P-gp) efflux transporter.  The proposed mechanism involves accelerated clearance via these routes due to enzalutamide-mediated induction.', 'DDInter', 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enzalutamide is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267783/', '', 'Etelcalcetide, Doxercalciferol, Salmon calcitonin, Calcifediol', 'Darolutamide, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, Nilutamide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241479, 'Cinacalcet', 'Osilodrostat', 'Moderate', 'Coadministration with osilodrostat may increase the plasma concentrations of drugs that are metabolized by CYP450 1A2, 2C19, 2D6, and/or 3A4.  Osilodrostat has been shown to be a moderate inhibitor of CYP450 1A2, a mild to borderline moderate inhibitor of CYP450 2C19, and a weak inhibitor of CYP450 2D6 and 3A4.', 'DDInter', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osilodrostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267787/', '', 'Etelcalcetide, Doxercalciferol, Salmon calcitonin, Calcifediol, Paricalcitol', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241480, 'Cinacalcet', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267789/', '', 'Etelcalcetide, Salmon calcitonin, Calcifediol', 'Entrectinib, Imatinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241481, 'Cinoxacin', 'Acetohexamide', 'Major', 'Quinolone antibiotics may interfere with the therapeutic effects of insulin and other antidiabetic agents.  The use of quinolones has been associated with disturbances in blood glucose homeostasis possibly stemming from effects on pancreatic beta cell ATP-sensitive potassium channels that regulate insulin secretion.  Both hyperglycemia and hypoglycemia have been reported, usually in diabetic patients receiving concomitant treatment with an oral hypoglycemic agent or insulin.', 'DDInter', 'Blood glucose should be closely monitored whenever quinolones are prescribed to diabetic patients, especially if they are elderly, have renal impairment, or are severely ill.  Due to the risk of profound and potentially life-threatening hypoglycemia, particular caution is advised during concomitant use of insulin and insulin secretagogues (e.g., sulfonylureas, meglitinides).  Patients should also be apprised of the increased risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately, discontinue the quinolone, and contact their physician.  Alternative antibiotics may need to be considered.', 'Others', 'Blood glucose should be closely monitored whenever quinolones are prescribed to diabetic patients, especially if they are elderly, have renal impairment, or are severely ill.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267791/', '', 'Simvastatin, Guar gum, Rosuvastatin', 'Enoxacin, Lomefloxacin, Grepafloxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241482, 'Anisindione', 'Cinoxacin', 'Major', 'Some quinolone antibiotics have been reported to potentiate the hypoprothrombinemic effect of warfarin and other coumarin anticoagulants.  The exact mechanism is unknown but may involve inhibition of coumarin metabolism and/or depletion of certain clotting factors due to suppression of vitamin K-producing intestinal flora.', 'DDInter', 'Given the potential for clinically significant interaction and even fatality in the occasional, susceptible patient, close monitoring is recommended if a quinolone antibiotic is prescribed during coumarin anticoagulant therapy.  The INR should be checked frequently and coumarin dosage adjusted accordingly, particularly following initiation or discontinuation of quinolone therapy in patients who are stabilized on their anticoagulant regimen.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.  The same precaution may be applicable during therapy with other oral anticoagulants (e.g., indandiones), although clinical data are lacking.', 'Synergism', 'Given the potential for clinically significant interaction and even fatality in the occasional, susceptible patient, close monitoring is recommended if a quinolone antibiotic is prescribed during coumarin anticoagulant therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267793/', '', 'Enoxacin, Delafloxacin, Lomefloxacin, Grepafloxacin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241483, 'Cinoxacin', 'Attapulgite', 'Moderate', 'Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics.  Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc.  The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.  When coadministered with Suprep Bowel Prep (magnesium/potassium/sodium sulfates), the manufacturer recommends administering fluoroquinolone antibiotics at least 2 hours before and not less than 6 hours after Suprep Bowel Prep to avoid chelation with magnesium.  Please consult individual product labeling for specific recommendations.', 'Absorption', 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267794/', '', 'Pectin, Activated charcoal', 'Enoxacin, Lomefloxacin, Grepafloxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241484, 'Cinoxacin', 'Axicabtagene ciloleucel', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.  The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', 'DDInter', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.  The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.  In six patients with newly diagnosed hematologic malignancy, treatment with various antineoplastic agents (cyclophosphamide, cytarabine, daunorubicin, doxorubicin, mitoxantrone, prednisolone, vincristine) decreased the mean peak serum concentration (Cmax) and area under the concentration-time curve (AUC 0 to 4 hours) of ciprofloxacin by approximately 46% each.  Data are not available for other quinolone antibiotics.', 'Absorption', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267795/', '', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Enoxacin, Lomefloxacin, Grepafloxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241485, 'Cinoxacin', 'Bifidobacterium longum infantis', 'Moderate', 'Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic.  It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations.', 'DDInter', 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267796/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241486, 'Cinoxacin', 'Bromfenac', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use.  The interaction has been reported most often with enoxacin.  It may occur with other fluoroquinolones as well, but is poorly documented.  The exact mechanism of interaction is unknown.  Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect.  Patients with a history of seizures may be at greater risk.', 'DDInter', 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267797/', '', 'Fluocinolone acetonide, Fluorometholone', 'Enoxacin, Lomefloxacin, Grepafloxacin', 1767369485);
