INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241496, 'Cinoxacin', 'Melphalan flufenamide', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.  The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', 'DDInter', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.  The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.  In six patients with newly diagnosed hematologic malignancy, treatment with various antineoplastic agents (cyclophosphamide, cytarabine, daunorubicin, doxorubicin, mitoxantrone, prednisolone, vincristine) decreased the mean peak serum concentration (Cmax) and area under the concentration-time curve (AUC 0 to 4 hours) of ciprofloxacin by approximately 46% each.  Data are not available for other quinolone antibiotics.', 'Absorption', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267822/', '', 'Bendamustine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241497, 'Cinoxacin', 'Oxtriphylline', 'Moderate', 'Some fluoroquinolones have been shown to decrease the hepatic metabolism of theophyllines.  The mechanism is inhibition of CYP450 1A2 hepatic metabolism.', 'DDInter', 'Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels.  Levofloxacin, sparfloxacin, and lomefloxacin have been reported to cause minor or no changes in theophylline levels and may be considered as alternatives.  Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeat to their physicians.', 'Metabolism', 'Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267829/', '', 'Gatifloxacin, Levofloxacin, Nalidixic acid, Gemifloxacin, Lomefloxacin, Moxifloxacin, Trovafloxacin, Delafloxacin, Sparfloxacin', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Roflumilast, Pentoxifylline, Zafirlukast, Bromotheophylline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241498, 'Cinoxacin', 'Phenylbutazone', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use.  The interaction has been reported most often with enoxacin.  It may occur with other fluoroquinolones as well, but is poorly documented.  The exact mechanism of interaction is unknown.  Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect.  Patients with a history of seizures may be at greater risk.', 'DDInter', 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267830/', '', 'Enoxacin, Lomefloxacin, Grepafloxacin', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Tolazoline, Rabeprazole, Dimethyl sulfoxide', 1767369485);
