INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241570, 'Heparin (flush)', 'Clopidogrel', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', 'DDInter', 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', 'Caution and monitoring (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/268257/', '', 'Acenocoumarol, Selexipag', 'Vorapaxar, Antithrombin III human, Protein C, Clopidogrel, Vitamin A, Ascorbic acid, Nandrolone, Potassium Iodide', 1767369485);
