nts (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/278923/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (242982, 'Anisindione', 'Sulfisoxazole', 'Major', 'Coadministration with a sulfonamide may increase the plasma concentrations and hypoprothrombinemic effects of coumarin anticoagulants.  The proposed mechanism is sulfonamide inhibition of coumarin metabolism via CYP450 2C9 and possibly also displacement of coumarin derivatives from plasma protein binding sites.', 'DDInter', 'Patients receiving coumarin anticoagulants should be closely monitored during concomitant therapy with sulfonamides.  The INR should be checked frequently and coumarin dosage adjusted accordingly, particularly following initiation or discontinuation of sulfonamide therapy in patients who are stabilized on their anticoagulant regimen.  The same precaution may be applicable during therapy with other oral anticoagulants (e.g., indandiones), although clinical data are lacking.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism, Distribution', 'Patients receiving coumarin anticoagulants should be closely monitored during concomitant therapy with sulfonamides.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/279082/', '', 'Sulfamethizole, Tamsulosin, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Trichlormethiazide, Ciclopirox, Vemurafenib, Probenecid, More', '', 1767369485);
