INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243149, 'Gemfibrozil', 'Macitentan', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 or moderate dual or combined inhibitors of CYP450 3A4 and CYP450 2C9 may increase the plasma concentrations of macitentan.  Macitentan is primarily metabolized by CYP450 3A4 and to a minor extent by CYP450 2C8, CYP450 2C9 and CYP450 2C19.', 'DDInter', 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4.  The product labeling recommends avoiding concomitant use with potent inhibitors (e.g., protease inhibitors, clarithromycin, cobicistat, conivaptan, delavirdine, itraconazole, ketoconazole, nefazodone, posaconazole, voriconazole).  The manufacturer of macitentan also recommends avoiding concomitant use with moderate dual inhibitors of CYP450 3A4 and 2C9 (e.g., fluconazole, amiodarone) or in combination with both a moderate CYP450 3A4 inhibitor and a moderate CYP450 2C9 inhibitor.', 'Metabolism', 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/279394/', '', 'Cholestyramine, Rosuvastatin, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Niacin, Ezetimibe, Atorvastatin, Colesevelam, More', 'Riociguat, Metyrosine, Tadalafil', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243150, 'Gemtuzumab ozogamicin', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/279397/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', '', 1767369485);
