INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243430, 'Mitotane', 'Lapatinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of lapatinib with potent CYP450 3A4 inducers should generally be avoided.  If coadministration is required, a gradual dosage increase of lapatinib should be considered.  The prescribing information recommends titrating the lapatinib dosage gradually from 1250 mg/day up to 4500 mg/day or from 1500 mg/day up to 5500 mg/day depending on the indication and patient tolerability.  Based on pharmacokinetic studies, this dosage recommendation is predicted to adjust the lapatinib systemic exposure (AUC) to the range observed without inducers.  However, clinical data are lacking.  The dosage of lapatinib should be reduced over approximately 2 weeks to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of lapatinib with potent CYP450 3A4 inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281410/', '', 'Futibatinib, Lenvatinib, Trametinib, Ribociclib, Afatinib, Dacomitinib, Alectinib, Binimetinib', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Niraparib, Selinexor, Aflibercept, Trastuzumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243431, 'Perampanel', 'Mefloquine', 'Moderate', 'Mefloquine may increase the risk of seizures and decrease the effectiveness of anticonvulsant drugs in patients with epilepsy.', 'DDInter', 'Because it can increase the risk of seizures in patients with epilepsy, mefloquine should only be used for curative treatment of malaria in such patients and only if there are compelling medical reasons for its use.  Patient clinical status and anticonvulsant blood levels should be closely monitored if mefloquine is prescribed.  Dosage adjustments may be required.  Patients should be advised to notify their physician if they experience a loss of seizure control.  Mefloquine should not be prescribed for malaria prophylaxis in patients with a history of convulsions.', 'Antagonism', 'Because it can increase the risk of seizures in patients with epilepsy, mefloquine should only be used for curative treatment of malaria in such patients and only if there are compelling medical reasons for its use.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281428/', '', 'Artesunate, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Halofantrine, Quinine, Primaquine, Proguanil, Sulfadoxine', 'Fenfluramine', 1767369485);
