INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243460, 'Acetohexamide', 'Penbutolol', 'Moderate', 'Specifically, inhibition of catecholamine-mediated glycogenolysis and glucose mobilization in association with beta-blockade can potentiate insulin-induced hypoglycemia in diabetics and delay the recovery of normal blood glucose levels.', 'DDInter', 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients.  Nevertheless, caution is advised if they are prescribed to patients treated with insulin or oral antidiabetic agents that can cause hypoglycemia (e.g., insulin secretagogues), as cardioselectivity is not absolute and larger doses of beta-1 selective agents may pose some of the same risks as nonselective agents.  Patients should be advised of the need for regular blood glucose monitoring and be aware that certain symptoms of hypoglycemia such as tremor and tachycardia may be masked.  However, other symptoms such as headache, dizziness, drowsiness, confusion, nausea, hunger, weakness, and perspiration may be unaffected.  The same precautions are applicable in diabetic patients treated with ophthalmic beta-blockers.', 'Synergism', 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281832/', '', 'Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Dulaglutide, Exenatide, Miglitol, Albiglutide, Rosiglitazone, Semaglutide, Pioglitazone, More', 'Carteolol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243461, 'Anisindione', 'Penbutolol', 'Minor', 'Some oral beta-blockers may increase serum levels of oral anticoagulants and enhance anticoagulant effects.  The mechanism is unknown .  Data have been conflicting.  Case reports exist for propranolol, while acebutolol, atenolol, betaxolol, bisoprolol, carvedilol, esmolol, metoprolol, and pindolol reportedly do not interact.  Hypoprothrombinemic effects are expected to be minimal.', 'DDInter', 'It is recommended that PT or INR be monitored when a beta-blocker is added to or deleted from the medical regimen of a patient receiving an oral anticoagulant.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', 'It is recommended that PT or INR be monitored when a beta-blocker is added to or deleted from the medical regimen of a patient receiving an oral anticoagulant.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281835/', '', 'Pindolol, Metoprolol, Atenolol, Acebutolol, Nadolol, Metoprolol, Carvedilol, Esmolol, Betaxolol, Nebivolol, Bisoprolol, More', '', 1767369485);
