INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243485, 'Isoetharine', 'Penbutolol', 'Major', 'Beta-blockers may antagonize the effects of beta-2 adrenergic bronchodilators and precipitate acute, life-threatening bronchospasm in patients with asthma or other obstructive airway diseases.  The mechanism involves increased airway resistance and reduced bronchodilation due to blockade of beta-2 adrenergic receptors.  The interaction may also occur with ophthalmically applied beta-blockers, which are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.  Due to opposing effects on beta-2 adrenergic receptors, propranolol has been used in the treatment of albuterol overdose.  Likewise, beta-2 adrenergic agonists may interfere with the pharmacologic effects of beta-blockers.', 'DDInter', 'Concomitant use of beta-2 adrenergic bronchodilators with beta-blockers, including ophthalmic formulations, should generally be avoided.  If coadministration is required, a cardioselective beta-blocker (e.g., acebutolol, atenolol, betaxolol, bisoprolol, metoprolol, nebivolol) is usually preferred.  Nevertheless, caution is advised and respiratory status should be closely monitored, as cardioselectivity is not absolute and larger doses of beta-1 selective agents may pose some of the same risks as nonselective agents.  In general, nonselective beta-blockers are considered contraindicated in patients with obstructive airways disease.', 'Antagonism', 'Concomitant use of beta-2 adrenergic bronchodilators with beta-blockers, including ophthalmic formulations, should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281871/', '', 'Carteolol', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243486, 'Penbutolol', 'Levobetaxolol (ophthalmic)', 'Moderate', 'Ophthalmic beta blocker solutions are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.  Additive effects of beta blockade may occur in patients receiving both oral and ophthalmic beta blocking agents.', 'DDInter', 'Blood pressure and intraocular pressure monitoring is recommended during concurrent use.  Patients should be advised to notify their physician if they experience a slow pulse, irregular heartbeat, dizziness, shortness of breath, or syncope.', 'Synergism', 'Blood pressure and intraocular pressure monitoring is recommended during concurrent use.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281872/', '', 'Carteolol', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243487, 'Penbutolol', 'Magaldrate', 'Minor', 'Concurrent administration with aluminum and magnesium antacids has been shown to decrease the oral bioavailability of certain beta-blockers, although data are conflicting.  The exact mechanism of interaction is unknown but may involve cation binding of beta-blockers or a reduction in the dissolution rate due to increased gastric pH.', 'DDInter', 'Based on available data, the clinical significance of this potential interaction is difficult to determine.  As a precaution, patients may want to consider separating the administration times of beta-blockers and antacids or other aluminum- or magnesium-containing products by at least 2 hours.', 'Absorption', 'Based on available data, the clinical significance of this potential interaction is difficult to determine.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281873/', '', 'Esmolol, Carteolol', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243488, 'Penbutolol', 'Mephentermine', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of pressor agents by blocking beta-1 adrenergic receptors in the heart.', 'DDInter', 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations.  A beta-blocker such as propranolol may be used to treat cardiac arrhythmias that occur during administration of pressor agents like mephentermine, metaraminol, norepinephrine, or phenylephrine.', 'Antagonism', 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281874/', '', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Methoxamine', 'Carteolol', 1767369485);
