INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243490, 'Penbutolol', 'Methacholine', 'Major', 'Patients who are concurrently receiving beta blockers may have exaggerated or prolonged responses (i.e., bronchoconstriction) to methacholine challenge and may not respond readily to treatment.  Beta-2-adrenergic blockade increases airway resistance.  Ophthalmically applied beta blockers undergo some systemic absorption and potentially may also interact.', 'DDInter', 'Administration of methacholine to a patient on any beta blocker therapy is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281876/', '', 'Methylene blue, Hexaminolevulinate, Secretin human, Sermorelin, Sorbitol, Galactose, Magnesium sulfate, Vitamin A, Caffeine, Metyrapone, Edrophonium, More', 'Nebivolol, Carteolol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243491, 'Penbutolol', 'Methdilazine', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity.', 'DDInter', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.  A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.  Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281877/', '', 'Ketotifen, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Fexofenadine, More', 'Carteolol', 1767369485);
