INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243747, 'Bepridil', 'Telaprevir', 'Major', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitor, telaprevir, may significantly increase the plasma concentrations of certain antiarrhythmic agents such as amiodarone, bepridil, disopyramide, and quinidine.  The mechanism involves inhibition of CYP450 3A4 metabolism, as telaprevir is a potent inhibitor.', 'DDInter', 'Caution is advised if telaprevir must be used with antiarrhythmic agents that are primarily metabolized by CYP450 3A4.  Pharmacologic response and plasma antiarrhythmic drug levels should be monitored more closely whenever the HCV NS3/4A protease inhibitor is added to or withdrawn from therapy, and the antiarrhythmic dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if telaprevir must be used with antiarrhythmic agents that are primarily metabolized by CYP450 3A4.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/284348/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', '', 1767369485);
