INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243895, 'Bepridil', 'Relugolix', 'Major', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of relugolix, particularly when the inhibitors are given orally.  Relugolix is a substrate for intestinal P-gp.  In vitro, it is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8.', 'DDInter', 'Concomitant use of relugolix with orally administered P-gp inhibitors should be avoided when possible.  In addition, the benefits of androgen deprivation therapy such as relugolix should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval, many of which are also P-gp inhibitors (e.g., amiodarone, azithromycin, bepridil, cabozantinib, clarithromycin, crizotinib, dronedarone, elagolix, erythromycin, ketoconazole, lapatinib, mifepristone, nilotinib, osimertinib, propafenone, quinidine, quinine, ranolazine, tacrolimus, telithromycin, valbenazine, vemurafenib).  If coadministration is required, the manufacturer recommends taking relugolix first and separating the dosing by at least 6 hours.  Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.  Alternatively, when relugolix is used as monotherapy for the treatment of prostate cancer, the prescribing information states that treatment with relugolix may be interrupted for up to two weeks if a short course of treatment with a P-gp inhibitor is necessary.  Following interruption of relugolix for more than 7 days, the manufacturer recommends restarting therapy with a loading dose of 360 mg on the first day, then continuing with a dose of 120 mg once daily.', 'Absorption', 'Concomitant use of relugolix with orally administered P-gp inhibitors should be avoided when possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/285238/', '', 'Norethisterone, Nafarelin, Elagolix, Cetrorelix, Estradiol, Ganirelix, Gonadorelin, Darolutamide, Anastrozole, Fulvestrant, Letrozole, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (243896, 'Butalbital', 'Relugolix', 'Minor', 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.  In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8.', 'DDInter', 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.  In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8.  Relugolix is also a substrate for intestinal P-gp.  When relugolix was coadministered with rifampin, a combined P-gp and potent CYP450 3A inducer, relugolix peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by 23% and 55%, respectively.  By contrast, no clinically significant differences in the pharmacokinetics of relugolix were observed when coadministered with enzalutamide, a strong CYP450 3A inducer that is not known to induce P-gp.', 'Metabolism, Absorption', 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/285240/', '', 'Triazolam, Flurazepam, Methohexital, Zolpidem, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, Paraldehyde, More', 'Nafarelin, Octreotide, Cetrorelix, Pasireotide, Ganirelix, Lanreotide, Gonadorelin, Anastrozole, Aminoglutethimide, Letrozole, Fulvestrant, More', 1767369485);
