INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (244050, 'Siponimod', 'Melphalan flufenamide', 'Major', 'Coadministration of siponimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may result in additive immune system effects and increased risk of infections.  Siponimod causes reversible sequestration of lymphocytes in lymphoid tissues.', 'DDInter', 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or other immune-modulating agents have not been evaluated.  Close monitoring for signs and symptoms of infection is advised during coadministration and for 3 to 4 weeks after the last dose of siponimod.  When switching from drugs with prolonged immune effects to siponimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or other immune-modulating agents have not been evaluated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/286091/', '', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, More', '', 1767369485);
