INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (244256, 'Enoxacin', 'Rofecoxib', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use.  The interaction has been reported most often with enoxacin.  It may occur with other fluoroquinolones as well, but is poorly documented.  The exact mechanism of interaction is unknown.  Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect.  Patients with a history of seizures may be at greater risk.', 'DDInter', 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/287660/', '', 'Rabeprazole, Phenylbutazone, Misoprostol, Glucosamine, Chondroitin sulfate, Esomeprazole', '', 1767369485);
