INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (244406, 'Boceprevir', 'Lopinavir', 'Major', 'Coadministration of boceprevir with cobicistat-boosted or ritonavir-boosted regimens of atazanavir, darunavir, or lopinavir may result in decreased plasma concentrations of both boceprevir and the protease inhibitors.  The mechanism of interaction has not been described.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, the use of boceprevir in combination with ritonavir-boosted or cobicistat-boosted regimens of atazanavir, darunavir, or lopinavir is not recommended.  Moreover, the safety and efficacy of boceprevir has not been established in the HCV/HIV coinfected population.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, the use of boceprevir in combination with ritonavir-boosted or cobicistat-boosted regimens of atazanavir, darunavir, or lopinavir is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288214/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, More', 'Lamivudine, Glecaprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (244407, 'Bosentan', 'Lopinavir', 'Moderate', 'Coadministration of a protease inhibitor (PI) in combination with bosentan may increase the plasma concentration of bosentan and decrease that of the PI, unless ritonavir is included as a pharmacokinetic booster.  Both PIs and bosentan are primarily metabolized by CYP450 3A4.  Because PIs are also inhibitors of CYP450 3A4, they can inhibit the metabolic clearance of bosentan.  Conversely, bosentan may induce the metabolism of PIs, although the effects are probably not significant in the presence of ritonavir, which is a potent CYP450 3A4 inhibitor.', 'DDInter', 'Protease inhibitors should generally not be used in combination with bosentan unless ritonavir is also added as a pharmacokinetic booster.  Dose adjustment of bosentan has been recommended.  In patients who are starting bosentan and have been receiving PI therapy for at least 10 days, it has been recommended to start bosentan at 62.5 mg once daily or every other day based upon individual tolerability.  When starting PI therapy in patients already on bosentan, it has been recommended to discontinue bosentan for at least 36 hours prior to initiation of PI therapy.  After at least 10 days following the initiation of PI therapy, bosentan may be resumed at 62.5 mg once daily or every other day based upon individual tolerability.  According to some authorities, use of the fixed combination atazanavir-cobicistat with bosentan is not recommended.', 'Metabolism', 'Protease inhibitors should generally not be used in combination with bosentan unless ritonavir is also added as a pharmacokinetic booster.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288215/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, More', 'Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Oseltamivir, Brincidofovir, Elbasvir, Famciclovir, Voxilaprevir, More', 1767369485);
