INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (244698, 'Saquinavir', 'Lopinavir', 'Major', 'Both saquinavir and lopinavir have been associated with prolongation of the QT interval when coadministered with ritonavir as a pharmacokinetic booster.  Theoretically, combining these medications may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'The product labeling for saquinavir and lopinavir-ritonavir both recommend that these medications not be used with other agents that can prolong the QT interval.  Whether they should be used with each other is unknown, but coadministration may increase the risk of QT and PR prolongation and should preferably be avoided if possible.  If concomitant use is necessary, the recommended dosages are saquinavir 1000 mg twice daily and lopinavir-ritonavir 400 mg-100 mg twice daily.  Once-daily dosing of lopinavir-ritonavir has not been studied for use in combination with saquinavir.  Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, palpitations, syncope, and/or irregular heartbeat.', 'Synergism', 'The product labeling for saquinavir and lopinavir-ritonavir both recommend that these medications not be used with other agents that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288525/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, More', 'Glecaprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Remdesivir, Famciclovir, Maribavir, Pibrentasvir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (244699, 'Saxagliptin', 'Lopinavir', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, sirolimus, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents.  These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.  Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.  Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.  Likewise, patients should be observed for hypoglycemia when these drugs are withdrawn from their therapeutic regimen.', 'Antagonism', 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288526/', '', 'Simvastatin, Guar gum', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Valaciclovir, Rilpivirine, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 1767369485);
