INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (244986, 'Fesoterodine', 'Benzhydrocodone', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects.', 'DDInter', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288841/', '', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Mirabegron, Acetohydroxamic acid, Yohimbine, Avanafil, Papaverine, Vardenafil, Phenyl salicylate, More', '', 1767369485);
